mapper.delete:
UPDATE BOOK_BOARD SET DELFLAG ='Y'
WHERE BOARD_SEQ IN

   #{board_seq}

DELETE FROM BOOK_BOARD a 
WHERE BOARD_SEQ IN

   #{board_seq}

mapper.select:
      SELECT *
		FROM (
			SELECT bb.*, ROW_NUMBER () OVER( ORDER BY REGDATE DESC) RNUM
				FROM BOOK_BOARD bb
			)
		WHERE RNUM BETWEEN #{start} AND #{last}
